import React from "react";
import { useSimpleErrors } from "simple-context-state";
import { useSimpleState } from "simple-context-state";
// import { useSimpleState } from "../../src/package";

function Errors() {
  const errors = useSimpleErrors();

  const { errors_clear } = useSimpleState();

  return errors ? (
    <div className="errorsContainer">
      <div className="errorsComponent">
        <div
          onClick={() => errors_clear("auth", "products")}
          className="errorsCloseButton"
        >
          X
        </div>
        <div className="errorsText">ERROR</div>
      </div>
    </div>
  ) : null;
}

export default Errors;
