import React from "react";
//import { useSimpleState } from "simple-context-state";
import { useSimpleStore } from "../../src/package";

function Consoles() {
  //Console Store
  const { consoles, consoles_add } = useSimpleStore("consoles");

  React.useEffect(() => {
    console.log("console rendered");
  }, []);

  return (
    <div className="listComponent">
      <h3>Consoles</h3>
      <button onClick={() => consoles_add("Dreamcast")}>Add</button>
      {consoles && consoles.map((c) => <p>{c}</p>)}
      <hr />
    </div>
  );
}

export default Consoles;
